import './HomePage.css';

function Article({ article }) {
    // Function to format time difference
    const getTimeDifference = (publishedAt) => {
        const now = new Date();
        const publishedTime = new Date(publishedAt);

        // Set both dates to midnight (00:00) to check if they are on the same day
        const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        const publishedDate = new Date(publishedTime.getFullYear(), publishedTime.getMonth(), publishedTime.getDate());

        // If the article was published today, calculate the difference
        if (today.getTime() === publishedDate.getTime()) {
            const diffInMs = now - publishedTime;
            const seconds = Math.floor(diffInMs / 1000);
            const minutes = Math.floor(seconds / 60);
            const hours = Math.floor(minutes / 60);
            const days = Math.floor(hours / 24);

            if (days > 0) {
                return `${days} day${days > 1 ? 's' : ''} ago`;
            } else if (hours > 0) {
                return `${hours} hr${hours > 1 ? 's' : ''} ago`;
            } else if (minutes > 0) {
                return `${minutes} min ago`;
            } else {
                return `${seconds} sec ago`;
            }
        } else {
            // If the article was not published today, show the full date and time
            return `Published on: ${publishedTime.toLocaleString()}`;
        }
    };

    return (
        <div className="article-card">
            <div className='tittle_div'>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="tittle">
                    {article.title}
                </a>
            </div>
            <p className='category'>
                <strong>Category:</strong> {article.category}
            </p>
            <p className='publish'>
                <strong>Published:</strong> {getTimeDifference(article.published_at)}
            </p>
            <p className='news'>{article.news}</p>
            <hr />
        </div>
    );
}

export default Article;
